  profile: personal
  region: us-west-2
  environment: ${file(./env.yml)}
  iamRoleStatements:
    - Effect: Allow
      Action:
        - s3:*
      Resource: arn:aws:s3:::${self:provider.environment.BUCKET_NAME}/photos/*
