service: puller

provider:
  name: aws
  runtime: nodejs10.x

functions:
  puller:
    handler: puller.puller
    environment:
      GITHUB_TOKEN: ${ssm:puller-githubToken~true}
      baseRepoOwner: ${ssm:puller-baseRepoOwner}
      repoName: ${ssm:puller-repoName}
      baseRepoBranch: ${ssm:puller-baseRepoBranch}
      headRepoOwner: ${ssm:puller-headRepoOwner}
      headRepoBranch: ${ssm:puller-headRepoBranch}
    events:
      - schedule: rate(1 hour)

service: ${file(./../../constants.yml):NAME}-example-service

# endpoints
functions:
  exampleCreate:
    handler: handler.exampleCreate
    events:
      - http:
          path: example
          method: post
          cors: true
      - schedule:
          name: ${file(./../../constants.yml):NAME}-exampleCreate-${opt:stage, 'dev'}
          rate: ${file(./../../constants.yml):AWS_WARMUP_RATE}
          enabled: ${file(./../../constants.yml):AWS_WARMUP}
          input:
            warmup: true
  exampleGet:
    handler: handler.exampleGet
    events:
      - http:
          path: example/{id}
          method: get
          cors: true
      - schedule:
          name: ${file(./../../constants.yml):NAME}-exampleGet-${opt:stage, 'dev'}
          rate: ${file(./../../constants.yml):AWS_WARMUP_RATE}
          enabled: ${file(./../../constants.yml):AWS_WARMUP}
          input:
            warmup: true
  exampleList:
    handler: handler.exampleList
    events:
      - http:
          path: example
          method: get
          cors: true
      - schedule:
          name: ${file(./../../constants.yml):NAME}-exampleList-${opt:stage, 'dev'}
          rate: ${file(./../../constants.yml):AWS_WARMUP_RATE}
          enabled: ${file(./../../constants.yml):AWS_WARMUP}
          input:
            warmup: true
  exampleUpdate:
    handler: handler.exampleUpdate
    events:
      - http:
          path: example/{id}
          method: put
          cors: true
      - schedule:
          name: ${file(./../../constants.yml):NAME}-exampleUpdate-${opt:stage, 'dev'}
          rate: ${file(./../../constants.yml):AWS_WARMUP_RATE}
          enabled: ${file(./../../constants.yml):AWS_WARMUP}
          input:
            warmup: true
  exampleDelete:
    handler: handler.exampleDelete
    events:
      - http:
          path: example/{id}
          method: delete
          cors: true
      - schedule:
          name: ${file(./../../constants.yml):NAME}-exampleDelete-${opt:stage, 'dev'}
          rate: ${file(./../../constants.yml):AWS_WARMUP_RATE}
          enabled: ${file(./../../constants.yml):AWS_WARMUP}
          input:
            warmup: true

# config
provider:
  name: aws
  runtime: ${file(./../../constants.yml):NODE_VERSION}
  stage: ${opt:stage, 'dev'}
  role: ${file(./../../serverless.env.yml):AWS_ROLE}
  memorySize: ${file(./../../constants.yml):AWS_MEMORY}
  timeout: ${file(./../../constants.yml):AWS_TIMEOUT}
  vpc:
    securityGroupIds:
      - ${file(./../../serverless.env.yml):VPC_SECURITY}
    subnetIds:
      - ${file(./../../serverless.env.yml):VPC_SUBNET1}
      - ${file(./../../serverless.env.yml):VPC_SUBNET2}
      - ${file(./../../serverless.env.yml):VPC_SUBNET3}
      - ${file(./../../serverless.env.yml):VPC_SUBNET4}
  apiGateway:
    restApiId: ${file(./../../serverless.env.yml):AWS_API_ID_${self:custom.environments.${self:provider.stage}}}
    restApiRootResourceId: ${file(./../../serverless.env.yml):AWS_API_ROOT_ID_${self:custom.environments.${self:provider.stage}}}
  environment:
    STAGE: ${self:provider.stage}
    DB_HOST: ${file(./../../serverless.env.yml):DB_HOST}
    DB_USER: ${file(./../../serverless.env.yml):DB_USER}
    DB_PASS: ${file(./../../serverless.env.yml):DB_PASS}
    DB_NAME: ${file(./../../serverless.env.yml):DB_NAME}

plugins:
  - serverless-webpack

custom:
  environments:
    dev: DEV
    prod: PROD
  webpack:
    webpackConfig: 'webpack.config.js'
    includeModules: true
    packager: 'npm'

service: oauth-workflow

custom:
  namespace: ${env:OW_NAMESPACE}
  shared_namespace: ${env:OW_SHARED_NAMESPACE, 'adobeio'}
  shared_oauth_package: oauth
  shared_cache_package: cache
  client_id: ${env:OAUTH_API_KEY, 'change-me'}
  client_secret: ${env:CLIENT_SECRET, 'change-me'}
  scopes: ${env:SCOPES, 'openid,AdobeID'}
  persistence: ${env:IMS_AUTH_PERSIST, 'false'}
  accessKeyId: ${env:AWS_ACCESS_KEY_ID, 'XXX'}
  secretAccessKey: ${env:AWS_SECRET_ACCESS_KEY, 'XXX'}
  base_url: https://runtime.adobe.io
  redirect_url: ${env:REDIRECT_URL, 'http://www.adobe.com'}
  # Cookie path after $(BASE_URL)/api/v1/web/
  cookie_path: ${env:COOKIE_PATH, self:custom.namespace}
  # package binding to the shared package
  adobe_oauth_package: ${env:LOCAL_AUTH_PACKAGE_BINDING, 'myauthp-shared'}
  adobe_cache_package: ${env:LOCAL_CACHE_PACKAGE_BINDING, 'mycachep-shared'}
  # package for the auth sequence
  adobe_auth_seq_package: ${env:LOCAL_AUTH_PACKAGE, 'myauthp'}
  auth_seq_name: ${self:custom.adobe_auth_seq_package}/authenticate

provider:
  name: openwhisk

plugins:
  - serverless-dotenv-plugin
  - serverless-openwhisk

functions:
  # adobe/authenticate sequence
  authenticate:
    name: ${self:custom.auth_seq_name}
    sequence:
      - /${self:custom.namespace}/${self:custom.adobe_oauth_package}/login
      - /${self:custom.shared_namespace}/${self:custom.shared_cache_package}/encrypt
      - /${self:custom.namespace}/${self:custom.adobe_oauth_package}/success
    annotations:
      web-export: true


resources:
  packages:
    adobe-oauth:
      name: ${self:custom.adobe_oauth_package}
      binding: /${self:custom.shared_namespace}/${self:custom.shared_oauth_package}
      parameters:
        auth_provider: "adobe-oauth2"
        auth_provider_name: "adobe"
        client_id: ${self:custom.client_id}
        client_secret: ${self:custom.client_secret}
        scopes: ${self:custom.scopes}
        persistence: ${self:custom.persistence}
        callback_url: ${self:custom.base_url}/api/v1/web/${self:custom.namespace}/${self:custom.auth_seq_name}
        redirect_url: ${self:custom.redirect_url}
        cookie_path: ${self:custom.cookie_path}
        cache_namespace: ${self:custom.namespace}
        cache_package: ${self:custom.adobe_cache_package}

    adobe:
      name: ${self:custom.adobe_auth_seq_package}
service: jwtauth-workflow

custom:
  package: ${env:OW_PACKAGE, 'change-me'}
  namespace: ${env:OW_NAMESPACE}
  shared_namespace: ${env:OW_SHARED_NAMESPACE, 'adobeio'}
  shared_oauth_package: oauth
  shared_cache_package: cache
  jwt_client_id: ${file(./jwt.json):jwt_client_id}
  jwt_client_secret: ${file(./jwt.json):jwt_client_secret}
  technical_account_id: ${file(./jwt.json):technical_account_id}
  org_id: ${file(./jwt.json):org_id}
  meta_scopes: ${file(./jwt.json):meta_scopes}
  private_key: ${file(./jwt.json):private_key}
  persistence: ${env:IMS_AUTH_PERSIST, 'false'}
  accessKeyId: ${env:AWS_ACCESS_KEY_ID, 'XXX'}
  secretAccessKey: ${env:AWS_SECRET_ACCESS_KEY, 'XXX'}
  base_url: https://runtime.adobe.io
  redirect_url: ${env:REDIRECT_URL, 'http://www.adobe.com'}
  adobe_auth_package: ${env:LOCAL_AUTH_PACKAGE_BINDING, 'jwtauthp-shared'}
  adobe_cache_package: ${env:LOCAL_CACHE_PACKAGE_BINDING, 'jwtcachep-shared'}
  adobe_auth_seq_package: ${env:LOCAL_AUTH_PACKAGE, 'jwtauthp'}
  jwtauth_seq_name: ${self:custom.adobe_auth_seq_package}/jwtauthenticate


provider:
  name: openwhisk

plugins:
  - serverless-dotenv-plugin
  - serverless-openwhisk

functions:
  # adobe/jwtauthenticate sequence
  jwtauthenticate:
    name: ${self:custom.jwtauth_seq_name}
    sequence:
      - /${self:custom.namespace}/${self:custom.adobe_auth_package}/jwtauth
    annotations:
      web-export: true

resources:
  packages:
    adobe-oauth:
      name: ${self:custom.adobe_auth_package}
      binding: /${self:custom.shared_namespace}/${self:custom.shared_oauth_package}
      parameters:
        jwt_client_id: ${self:custom.jwt_client_id}
        jwt_client_secret: ${self:custom.jwt_client_secret}
        technical_account_id: ${self:custom.technical_account_id}
        org_id: ${self:custom.org_id}
        meta_scopes: ${self:custom.meta_scopes}
        private_key: ${self:custom.private_key}
        persistence: ${self:custom.persistence}
        redirect_url: ${self:custom.redirect_url}
        cache_namespace: ${self:custom.namespace}
        cache_package: ${self:custom.adobe_cache_package}

    adobe:
      name: ${self:custom.adobe_auth_seq_package}
