files:
service: jira-github-api

provider:
  name: aws
  runtime: nodejs8.10

plugins:
  - serverless-dotenv-plugin

# you can add packaging information here
package:
  include:
    - pr-lambda.js
    - branch-lambda.js
    - pr-handler.js
    - branch-handler.js
    - client.js
    - node_modules/**

functions:
  pr-github-jira-hook:
    handler: pr-lambda.handler
    environment:
      JIRA_DOMAIN: ${env:JIRA_DOMAIN}
      TOKEN: ${env:TOKEN}
      USERNAME: ${env:USERNAME}
      GITHUB_PR_OPEN: ${env:GITHUB_PR_OPEN}
      GITHUB_BRANCH_OPEN: ${env:GITHUB_BRANCH_OPEN}
      GITHUB_PR_MERGED: ${env:GITHUB_PR_MERGED}

  branch-github-jira-hook:
    handler: branch-lambda.handler
    environment:
      JIRA_DOMAIN: ${env:JIRA_DOMAIN}
      TOKEN: ${env:TOKEN}
      USERNAME: ${env:USERNAME}
      GITHUB_PR_OPEN: ${env:GITHUB_PR_OPEN}
      GITHUB_BRANCH_OPEN: ${env:GITHUB_BRANCH_OPEN}
      GITHUB_PR_MERGED: ${env:GITHUB_PR_MERGED}

service:
  name: newsbot

custom: ${file(./config.${opt:stage}.js)}

provider:
  name: aws
  runtime: nodejs8.10
  profile: ${self:custom.aws.profile}
  stage: ${opt:stage}
  region: ${self:custom.aws.region}
  role: ${self:custom.aws.role}
  description: News Feeder via Serverless
  environment:
    STAGE: ${opt:stage}

package:
  exclude:
    - .git/**
    - "**test**"

functions:
  feed:
    handler: index.feed
    events:
      - schedule: rate(${self:custom.newsapi.refresh} minutes)

service: at2gh

plugins:
  - serverless-secrets-plugin

custom:
  secrets: ${file(secrets.${opt:stage, self:provider.stage}.yml)}

provider:
  name: aws
  runtime: nodejs8.10
  environment:
    GITHUB_API_KEY: ${self:custom.secrets.GITHUB_API_KEY}
    AIRTABLE_API_KEY: ${self:custom.secrets.AIRTABLE_API_KEY}
    REPO_OWNER: ${self:custom.secrets.REPO_OWNER}
    REPO_NAME: ${self:custom.secrets.REPO_NAME}
    AIRTABLE_BASE: ${self:custom.secrets.AIRTABLE_BASE}
    AIRTABLE_LINK_PRE: ${self:custom.secrets.AIRTABLE_LINK_PRE}

functions:
  # init:
  #   handler: handler.init
  cron:
    handler: handler.transferTasks
    events:
      - schedule: rate(10 minutes)
